mapper.select:
select * from (select qna.*, row_number() over(order by qna_seq desc)
rn from
qna) where rn between #{start} and #{end}

mapper.update:
update qna set qna_title = #{qna_title}, qna_contents
= #{qna_contents},
qna_write_date = sysdate where qna_seq = #{qna_seq}

mapper.select:
